(3, 'index', '<!doctype html>\r\n<html lang="en">\r\n    <head>\r\n        <meta charset="UTF-8">\r\n        <meta http-equiv="X-UA-Compatible" content="IE=edge,chrome=1">\r\n        <meta name=''keywords'' content=''{$config.web_keywords}''/>\r\n        <meta name=''description'' content=''{$config.web_description}''/>\r\n        <title>{$config.web_title}</title>\r\n        <link rel=''stylesheet'' href=''__CSS__/style.css'' type=''text/css'' />\r\n        <script src="__JS__/jquery-1.8.3.min.js"></script>\r\n\r\n        <link rel=''stylesheet'' href=''__CSS__/swiper4.5.css'' type=''text/css'' />\r\n        <script src="__JS__/Swiper4.5.0.js"></script>\r\n\r\n        <!-- animate -->\r\n        <link rel=''stylesheet'' href=''__CSS__/aos.css'' type=''text/css'' />\r\n\r\n\r\n    </head>\r\n    <body>\r\n\r\n\r\n        <!-- header -->\r\n        <include file="head" title="公共头部"/>\r\n\r\n\r\n\r\n        <!-- banner -->\r\n        <div class="banner">\r\n            <div class="swiper-container banner_swiper">\r\n                <ul class="swiper-wrapper">\r\n                    <li class="swiper-slide">\r\n                        <img src="__ROOT__/{$adv.slide_pic}">\r\n                        <div class="txt">{$adv.slide_description}</div>\r\n                    </li>\r\n                </ul>\r\n            </div>\r\n            <div class="trolley"></div>\r\n        </div>\r\n        <script>\r\n            $(window).scroll(function() {\r\n                //console.log($(window).scrollTop());\r\n                //$(".banner_swiper").css({"transform":"translateY(-"+Math.max(0,$(window).scrollTop()-0)+"px)"});\r\n                $(".banner_swiper li .txt").css({\r\n                    "margin-top": -$(window).scrollTop() / 2\r\n                });\r\n                $(".trolley").css({\r\n                    "bottom": -$(window).scrollTop()\r\n                });\r\n                $(".banner_swiper").css({\r\n                    "transform": "translateY(" + Math.max(0, $(window).scrollTop() - 0) + "px)"\r\n                });\r\n\r\n            });\r\n        </script>\r\n        <script>\r\n            var mySwiper01 = new Swiper(''.banner_swiper'', {\r\n                loop: false,\r\n                speed: 1300,\r\n                slidesPerView: 1,\r\n                autoplay: {\r\n                    enabled: true,\r\n                    delay: 4000,\r\n                    disableOnInteraction: false,\r\n                },\r\n            });\r\n            mySwiper01.el.onmouseover = function() {\r\n                mySwiper01.autoplay.stop();\r\n            }\r\n            mySwiper01.el.onmouseleave = function() {\r\n                mySwiper01.autoplay.start();\r\n            }\r\n        </script>\r\n\r\n\r\n\r\n        <div class="home_bg">\r\n\r\n            <!-- 关于我们 -->\r\n            <div class="aboutA" aos="zoom-in" aos-duration="1500">\r\n                <div class="box">\r\n                    <div class="aboutA_t">关于我们</div>\r\n                    <div class="aboutA_c">{$about.content|mb_substr=0,330,''utf-8''}</div>\r\n                    <div class="aboutA_b">\r\n                        <div class="menu">\r\n                            <div class="b clearfix">\r\n                                <volist name=''nav'' id=''v'' offset="0" length=''1''>\r\n                                <a href="javascript:;" class="active">{$v.title}</a>\r\n                                </volist>\r\n                                <volist name=''nav'' id=''v'' offset="1" length=''5''>\r\n                                <a href="javascript:;">{$v.title}</a>\r\n                                </volist>\r\n                            </div>\r\n                        </div>\r\n                        <div class="cont">\r\n                            <volist name=''nav'' id=''v'' offset="0" length=''1''>\r\n                            <div class="item">{$v.description}</div></volist>\r\n                             <volist name=